sysauto_script.script:
// get today's date and time
var rightNow = new GlideDateTime();

//Query the database for Neeit tasks with due date field value order than 
// the current time. Only return task records that do not have state value of // closed Complete
var gr = new GlideRecord('x_58872_needit_needit_task');
gr.addQuery('due_date','<=',rightNow);
gr.addQuery('state','!=',3);
gr.query();

// Write a log message for each overdue NeedIt Task Record
while(gr.next()){		gs.eventQueue('x_58872_needit.overdueNeedItTask',gr,gr.number,gs.getUserID());
	gs.info("Overdue NeedIt Task record = " + gr.number);
}
